import React, { useEffect, useRef } from 'react';
import { Container, Typography, Box } from '@mui/material';
import { gsap } from 'gsap';

function Home() {
  const helloRef = useRef(null);
  const nameRef = useRef(null);

  useEffect(() => {
    const tl = gsap.timeline();

    tl.fromTo(helloRef.current,
      { 
        y: -50, 
        scaleY: -1, // This flips the text vertically
        opacity: 0 
      },
      { 
        duration: 1.5, 
        y: 0, 
        scaleY: 1, // This brings it back to normal
        opacity: 1, 
        ease: "power2.out" 
      }
    );

    tl.fromTo(nameRef.current,
      { x: 200, opacity: 0 },
      { duration: 1, x: 0, opacity: 1, ease: "power2.out" },
      "-=0.5"
    );
  }, []);

  return (
    <Container>
      <Box my={4}>
        <Typography variant="h2" component="h1" ref={helloRef} style={{ display: 'inline-block', transformOrigin: 'center' }}>
          Hello,
        </Typography>
        <Typography variant="h2" component="h1" gutterBottom>
          <span ref={nameRef} style={{ display: 'inline-block' }}> I'm [Your Name]</span>
        </Typography>
        <Typography variant="h5" component="h2" gutterBottom>
          A [Your Profession] passionate about creating amazing web experiences.
        </Typography>
      </Box>
    </Container>
  );
}

export default Home;
